func pushAndroid(excelFileName string) {
	xlFile, err := xlsx.OpenFile(excelFileName)
	if err != nil {
		fmt.Printf("open failed: %s\n", err)
	}
	sheet := xlFile.Sheets[0]
	fmt.Printf("Sheet Name: %s\n", sheet.Name)
	for index, row := range sheet.Rows {
		cell := row.Cells[0]
		text := cell.String()
		callAndiordPush(text)
		fmt.Printf("用户ID [%s] 第 [%d] 个 android 用户ID的通知\n", text, index)
	}
}

func pushIos(excelFileName string) {
	xlFile, err := xlsx.OpenFile(excelFileName)
	if err != nil {
		fmt.Printf("open failed: %s\n", err)
	}
	sheet := xlFile.Sheets[0]
	fmt.Printf("Sheet Name: %s\n", sheet.Name)
	for index, row := range sheet.Rows {
		cell := row.Cells[0]
		text := cell.String()
		callIosPush(text)
		fmt.Printf("用户ID [%s] 第 [%d] 个 ios 用户ID的通知\n", text, index)
	}

}

